WITH raw_series_catalog AS (
    SELECT
        tenant_id,
        provider,
        series_id,
        dataset_code,
        title,
        description,
        unit_code,
        frequency_code,
        geo_id,
        status,
        category,
        source_url,
        notes,
        start_ts,
        end_ts,
        last_observation_ts,
        asof_date,
        created_at,
        updated_at,
        ingest_ts,
        ingest_job_id,
        ingest_run_id,
        ingest_batch_id,
        tags,
        metadata,
        iso_code,
        iso_market,
        iso_product,
        iso_location_type,
        iso_location_id,
        iso_location_name,
        iso_timezone,
        iso_interval_minutes,
        iso_unit,
        iso_subject,
        iso_curve_role,
        version,
        -- Add row number to handle duplicates based on natural key + version
        ROW_NUMBER() OVER (
            PARTITION BY tenant_id, provider, series_id, COALESCE(version, 0)
            ORDER BY ingest_ts DESC
        ) as rn
    FROM {{ source('iceberg_external', 'series_catalog') }}
)

SELECT
    tenant_id,
    provider,
    series_id,
    dataset_code,
    title,
    description,
    unit_code,
    frequency_code,
    geo_id,
    status,
    category,
    source_url,
    notes,
    start_ts,
    end_ts,
    last_observation_ts,
    asof_date,
    created_at,
    updated_at,
    ingest_ts,
    ingest_job_id,
    ingest_run_id,
    ingest_batch_id,
    tags,
    metadata,
    iso_code,
    iso_market,
    iso_product,
    iso_location_type,
    iso_location_id,
    iso_location_name,
    iso_timezone,
    iso_interval_minutes,
    iso_unit,
    iso_subject,
    iso_curve_role,
    version
FROM raw_series_catalog
WHERE rn = 1
